import React, { useState, useEffect } from "react";
import { View, Text, TouchableOpacity, TextInput, Image, StyleSheet } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import * as ImagePicker from 'expo-image-picker';
import AsyncStorage from '@react-native-async-storage/async-storage';
import firebase from 'firebase/app';
import 'firebase/database';
import firebaseConfig from '../navigation/AppNavigator'; // Importar o arquivo firebaseConfig.js que você criou

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

export default function ProfileScreen({ navigation }) {
  const isDarkmode = false; // Assuming isDarkmode is set to false
  const [profileImage, setProfileImage] = useState(null);
  const [profileFields, setProfileFields] = useState([
    { key: "name", label: "Name", value: "" },
    { key: "number", label: "Number", value: "" },
    { key: "email", label: "Email", value: "" },
    { key: "dob", label: "Date of Birth", value: "" },
    { key: "cpf", label: "CPF", value: "" },
    { key: "clinicName", label: "Clinic Name", value: "" },
  ]);

  const handleBack = () => {
    navigation.goBack();
  };

  const handleSaveButton = () => {
    saveDataToFirebase();
    // Você também pode exibir uma mensagem para o usuário informando que os dados foram salvos
    // ou navegar para outra tela após salvar os dados, conforme sua necessidade.
  };

  const saveDataToFirebase = () => {
    firebase.database().ref('profileData').set(profileFields)
      .then(() => console.log('Data saved to Firebase.'))
      .catch(error => console.error('Error saving data to Firebase:', error));
  };

  const handleFieldChange = (key, value) => {
    setProfileFields(prevFields =>
      prevFields.map(field => (field.key === key ? { ...field, value } : field))
    );
    saveDataLocally(); // Salvando os dados localmente ao alterar um campo
  };

  // Função para salvar os dados localmente no AsyncStorage
  const saveDataLocally = async () => {
    try {
      await AsyncStorage.setItem('@profileData', JSON.stringify(profileFields));
      console.log('Data saved locally.');
    } catch (error) {
      console.error('Error saving data locally:', error);
    }
  };

  // Função para carregar os dados localmente do AsyncStorage
  const loadLocalData = async () => {
    try {
      const data = await AsyncStorage.getItem('@profileData');
      if (data) {
        setProfileFields(JSON.parse(data));
        console.log('Data loaded from local storage.');
      }
    } catch (error) {
      console.error('Error loading data from local storage:', error);
    }
  };

  // Carregar os dados locais ao abrir a tela
  useEffect(() => {
    loadLocalData();
  }, []);

  const handleChoosePhoto = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });

    if (!result.cancelled) {
      setProfileImage(result.uri);
    }
  };

  const handleRemovePhoto = () => {
    setProfileImage(null);
  };

  return (
    <View style={styles.container}>
      <View style={[styles.header, { backgroundColor: isDarkmode ? "#000" : "#fff" }]}>
        <Ionicons
          name="chevron-back"
          size={20}
          color={isDarkmode ? "#fff" : "#000"}
          onPress={handleBack}
        />
        <Text style={[styles.headerText, { color: isDarkmode ? "#fff" : "#000" }]}>Perfil</Text>
      </View>
      <View style={styles.content}>
        <View style={styles.profileImageContainer}>
          {profileImage ? (
            <Image source={{ uri: profileImage }} style={styles.profileImage} />
          ) : (
            <TouchableOpacity onPress={handleChoosePhoto}>
              <Text style={styles.profileImagePlaceholder}>Escolha uma Foto</Text>
            </TouchableOpacity>
          )}
          {profileImage && (
            <View style={styles.profileImageButtons}>
              <TouchableOpacity onPress={handleChoosePhoto}>
                <Text style={styles.editButton}>Edit</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={handleRemovePhoto}>
                <Text style={styles.removeButton}>Remove</Text>
              </TouchableOpacity>
            </View>
          )}
        </View>
        {profileFields.map(field => (
          <View key={field.key} style={styles.profileField}>
            <Text style={styles.fieldLabel}>{field.label}:</Text>
            <TextInput
              style={styles.fieldInput}
              placeholder={`Enter your ${field.label.toLowerCase()}`}
              value={field.value}
              onChangeText={value => handleFieldChange(field.key, value)}
            />
          </View>
        ))}
        <TouchableOpacity style={styles.saveButton} onPress={handleSaveButton}>
          <Text style={styles.saveButtonText}>Salvar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    paddingVertical: 10,
    paddingHorizontal: 15,
  },
  headerText: {
    flex: 1,
    textAlign: "center",
    fontSize: 18,
    fontWeight: "bold",
  },
  content: {
    flex: 1,
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 10,
  },
  profileImageContainer: {
    alignItems: "center",
    marginBottom: 20,
  },
  profileImage: {
    width: 120,
    height: 120,
    borderRadius: 60,
  },
  profileImagePlaceholder: {
    width: 120,
    height: 120,
    borderRadius: 60,
    borderWidth: 1,
    borderColor: "#ccc",
    textAlign: "center",
    textAlignVertical: "center",
    fontSize: 16,
  },
  profileImageButtons: {
    flexDirection: "row",
    marginTop: 10,
  },
  editButton: {
    color: "#007BFF",
    marginRight: 15,
  },
  removeButton: {
    color: "#FF0000",
  },
  profileField: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 15,
  },
  fieldLabel: {
    marginRight: 10,
    fontSize: 16,
  },
  fieldInput: {
    flex: 1,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    padding: 10,
    fontSize: 16,
  },
});
